        protected function queryNodeLink($json_string)
        {
                //uncomment the line below and remove the function argument if testing from backend
                
                $json = CJSON::decode($json_string);

                $routes = array();
                foreach ($json['routes'] as $routeid=>$route) 
                 {
                    $points = array();
                    foreach ($route['legs'] as $leg) {
                        $latitude = $leg['steps'][0]['start_location']['pb'];
                        $longitude = $leg['steps'][0]['start_location']['qb'];
                        $points[] = array(
                            'latitude' => $latitude,
                            'longitude' => $longitude,
                            'major' => 'true'
                        );
                        foreach ($leg['steps'] as $step) 
                        {
                            $latitude = $step['end_location']['pb'];
                            $longitude = $step['end_location']['qb'];
                            $points[] = array(
                                'latitude' => $latitude,
                                'longitude' => $longitude,
                                'major' => 'true'
                            );
                        }
                    }
                    $routes[] = array(
                        'route' => $routeid,
                        'points' => $points
                    );
                }
                $nodeArray = ThreatType::model()->getNodeLinkData($routes);
                
                $max = 0;
                foreach($nodeArray as $node)
                {
                    if ($max < $node['count'])
                    {
                        $max = $node['count'];
                    }
                }
                for ($i=0; $i<count($nodeArray); $i++)
                {   
                    $nodeArray[$i]['count'] = ($nodeArray[$i]['count']/$max)*15 + 15;
                }
                
                return $nodeArray;
        }

        protected function parseRoutes($json_string) {
            $routes = CJSON::decode($json_string);
            $parsed_route = array();
            foreach ($routes['routes'] as $route) {
                foreach ($route['legs'] as $leg) {
                        $latitude = $leg['steps'][0]['start_location']['pb'];
                        $longitude = $leg['steps'][0]['start_location']['qb'];
                        $points[] = array(
                            'latitude' => $latitude,
                            'longitude' => $longitude,
                            'major' => 'true'
                        );
                        foreach ($leg['steps'] as $step) {
                            foreach ($step['path'] as $path) {
                                $latitude = $path['pb'];
                                $longitude = $path['qb'];
                                $points[] = array(
                                    'latitude' => $latitude,
                                    'longitude' => $longitude,
                                    'major' => 'false'
                                );
                            }
                            $latitude = $step['end_location']['pb'];
                            $longitude = $step['end_location']['qb'];
                            $points[] = array(
                                'latitude' => $latitude,
                                'longitude' => $longitude,
                                'major' => 'true'
                            );
                        }
                    }
                    $route = array( 'points' => $points );
                    $parsed_route[] =  $route;
            }
            return $parsed_route;
        }
        
